import React, { useState } from 'react';
import { Container, Typography, TextField, Button, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    const loginUser = {
      username,
      password,
    };
  
    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginUser),
      });
  
      if (!response.ok) {
        const text = await response.text();
        console.error("Error response:", text);
        alert('Login failed: ' + text);
        return;
      }
  
      // Parse the JSON response
      const data = await response.json();
      console.log("Response data:", data); // Log the entire data object
      console.log("Message:", data.message); // Log only the message
  
      // Check if there's a success message and navigate
      if (data.message === "Login successful") {
        alert(data.message);
        navigate('/Dashboard');
      } else {
        alert('Unexpected response');
      }
    } catch (error) {
      console.error("Error:", error);
      alert('Error: ' + error.message);
    }
  };
  
  return (
    <Container maxWidth="sm" style={{ padding: '2rem' }}>
      <Box component="form" onSubmit={handleLogin}> {/* Form wrapper */}
        <Typography variant="h4" gutterBottom>
          Login
        </Typography>

        <TextField
          fullWidth
          label="Username"
          variant="outlined"
          margin="normal"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <TextField
          fullWidth
          label="Password"
          type="password"
          variant="outlined"
          margin="normal"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit" // Makes this button submit the form
          style={{ marginTop: '1rem' }}
        >
          Login
        </Button>
      </Box>
    </Container>
  );
};

export default Login;
