import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import "./Interview.css"; // new CSS for interview page
import { useLocation } from "react-router-dom";

function Interview() {
  const location = useLocation();
  const { resumeData } = location.state; // passed from ResumeUpload
  const videoRef = useRef(null);
  const [interviewStarted, setInterviewStarted] = useState(false);
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [timer, setTimer] = useState(60); // 60s per question

  // Proctoring & webcam
  useEffect(() => {
    if (!interviewStarted) return;

    // Request camera + mic with small preview
    navigator.mediaDevices.getUserMedia({
      video: { width: 160, height: 120 },
      audio: true,
    })
      .then((stream) => {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
        startSnapshotInterval(stream);
      })
      .catch((err) => {
        console.error("Camera error:", err);
        alert("Failed to access camera or microphone");
      });

    // Fullscreen
    document.documentElement.requestFullscreen().catch(() => {});

    // Tab/window activity tracking
    window.addEventListener("blur", handleBlur);
    window.addEventListener("focus", handleFocus);

    return () => {
      window.removeEventListener("blur", handleBlur);
      window.removeEventListener("focus", handleFocus);
      document.exitFullscreen().catch(() => {});
    };
  }, [interviewStarted]);

  useEffect(() => {
    if (timer === 0 && currentIndex < questions.length - 1) {
      setCurrentIndex((prev) => prev + 1);
      setTimer(60);
    } else if (timer > 0) {
      const interval = setInterval(() => setTimer((t) => t - 1), 1000);
      return () => clearInterval(interval);
    }
  }, [timer, currentIndex, questions]);

  const handleBlur = () => sendActivity("blur");
  const handleFocus = () => sendActivity("focus");

  const sendActivity = async (type) => {
    try {
      await axios.post("http://localhost:8080/interview/log-activity", {
        candidateId: resumeData.email,
        timestamp: Date.now(),
        type,
      });
    } catch (err) {
      console.error("Activity log failed:", err);
    }
  };

  const startSnapshotInterval = (stream) => {
    const canvas = document.createElement("canvas");
    canvas.width = 160;
    canvas.height = 120;
    const ctx = canvas.getContext("2d");

    setInterval(async () => {
      ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
      const imageBase64 = canvas.toDataURL("image/jpeg");
      try {
        await axios.post("http://localhost:8080/interview/snapshot", {
          candidateId: resumeData.email,
          timestamp: Date.now(),
          imageBase64,
        });
      } catch (err) {
        console.error("Snapshot upload failed:", err);
      }
    }, 5000); // every 5s
  };

  const startInterview = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8080/interview/generate",
        { skills: resumeData.skills }
      );
      setQuestions(response.data);
      setInterviewStarted(true);
    } catch (err) {
      console.error("Failed to generate questions:", err);
    }
  };

  const handleAnswerChange = (e) => {
    setAnswers({ ...answers, [currentIndex]: e.target.value });
  };

  const submitInterview = async () => {
    try {
      await axios.post("http://localhost:8080/interview/submit", {
        candidateId: resumeData.email,
        answers,
      });
      alert("Interview submitted successfully!");
      document.exitFullscreen();
    } catch (err) {
      console.error("Submission failed:", err);
    }
  };

  if (!interviewStarted) {
    return (
      <div className="interview-start">
        <h2>Welcome {resumeData.name}</h2>
        <button onClick={startInterview}>Start Interview</button>
      </div>
    );
  }

  const currentQuestion = questions[currentIndex];

  return (
    <div className="interview-container">
      <video
        ref={videoRef}
        className="cam-preview"
        width={160}
        height={120}
      />
      <div className="question-section">
        <h3>Question {currentIndex + 1}/{questions.length}</h3>
        <p><strong>Difficulty:</strong> {currentQuestion?.difficulty}</p>
        <p>{currentQuestion?.question}</p>
        <textarea
          value={answers[currentIndex] || ""}
          onChange={handleAnswerChange}
          placeholder="Type your answer here..."
        />
        <div className="timer">Time left: {timer}s</div>
        {currentIndex === questions.length - 1 ? (
          <button onClick={submitInterview}>Submit Interview</button>
        ) : (
          <button onClick={() => { setCurrentIndex((prev) => prev + 1); setTimer(60); }}>
            Next
          </button>
        )}
      </div>
    </div>
  );
}

export default Interview;
