import React, { useState } from 'react';
import { Container, Typography, TextField, Button, Box } from '@mui/material';

const Contact = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async () => {
    const formData = new URLSearchParams();
    formData.append("name", name);
    formData.append("email", email);
    formData.append("message", message);

    try {
      const response = await fetch('https://cors-anywhere.herokuapp.com/https://script.google.com/macros/s/AKfycbxmAQv0W4WD53h0HQNWwWn5u0Eh0g-dYk3WZN6RF6E/dev', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: formData.toString(), // Updated to use formData directly
      });

      const data = await response.json(); // Parsing response as JSON

      if (response.ok) {
        alert(data.message);  // Show success message from response
        setName('');
        setEmail('');
        setMessage('');
      } else {
        alert('Failed to send message: ' + data.message);  // Show error message from response
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error submitting form: ' + error.message);
    }
  };

  return (
    <Container maxWidth="sm" style={{ padding: '2rem' }}>
      <Box>
        <Typography variant="h4" gutterBottom>
          Contact Us
        </Typography>
        <Typography variant="body1" paragraph>
          If you have any questions or feedback, feel free to reach out to us.
        </Typography>

        <TextField
          fullWidth
          label="Your Name"
          variant="outlined"
          margin="normal"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <TextField
          fullWidth
          label="Your Email"
          variant="outlined"
          margin="normal"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <TextField
          fullWidth
          label="Your Message"
          variant="outlined"
          margin="normal"
          multiline
          rows={4}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          style={{ marginTop: '1rem' }}
          onClick={handleSubmit}
        >
          Send Message
        </Button>
      </Box>
    </Container>
  );
};

export default Contact;
