import React, { useEffect, useState, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Interview.css';

function Interview() {
  const location = useLocation();
  const navigate = useNavigate();
  const { resumeData } = location.state || {};

  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [fullscreen, setFullscreen] = useState(false);

  const videoRef = useRef(null);

  // ----------------- Initialization -----------------
  useEffect(() => {
    if (!resumeData) {
      navigate('/');
      return;
    }

    // Get webcam & microphone access
    const initCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        alert('Camera and microphone access required for proctored interview!');
      }
    };

    initCamera();

    // Fetch interview questions from backend
    const fetchQuestions = async () => {
      try {
        const response = await axios.post('http://localhost:8080/interview/generate', { skills: resumeData.skills });
        setQuestions(response.data); // [{question, difficulty}]
      } catch (err) {
        console.error(err);
      }
    };
    fetchQuestions();

    // Fullscreen
    const enterFullscreen = () => {
      if (document.documentElement.requestFullscreen) {
        document.documentElement.requestFullscreen();
        setFullscreen(true);
      }
    };
    enterFullscreen();

    // Activity tracking
    const handleBlur = () => alert('You switched tab/window! Stay focused.');
    const handleResize = () => console.log('Window resized!');
    const handleCopyPaste = (e) => e.preventDefault();

    window.addEventListener('blur', handleBlur);
    window.addEventListener('resize', handleResize);
    window.addEventListener('copy', handleCopyPaste);
    window.addEventListener('paste', handleCopyPaste);

    return () => {
      window.removeEventListener('blur', handleBlur);
      window.removeEventListener('resize', handleResize);
      window.removeEventListener('copy', handleCopyPaste);
      window.removeEventListener('paste', handleCopyPaste);
    };
  }, [resumeData, navigate]);

  // ----------------- Question Handling -----------------
  const handleAnswerChange = (e) => {
    setAnswers({ ...answers, [currentIndex]: e.target.value });
  };

  const handleNext = () => {
    if (currentIndex < questions.length - 1) setCurrentIndex(currentIndex + 1);
  };

  const handlePrevious = () => {
    if (currentIndex > 0) setCurrentIndex(currentIndex - 1);
  };

  const handleSubmit = () => {
    console.log('Submitted Answers:', answers);
    alert('Interview submitted successfully!');
    navigate('/');
  };

  if (!resumeData) return null;

  return (
    <div className="interview-container">
      <h2>Proctored Interview</h2>

      {/* Webcam */}
      <video ref={videoRef} autoPlay muted className="webcam-feed" />

      {/* Question Card */}
      {questions.length === 0 ? (
        <p>Loading questions...</p>
      ) : (
        <div className="question-card">
          <h3>Question {currentIndex + 1} of {questions.length}</h3>
          <p><strong>[{questions[currentIndex].difficulty}]</strong> {questions[currentIndex].question}</p>
          <textarea
            value={answers[currentIndex] || ''}
            onChange={handleAnswerChange}
            placeholder="Type your answer here..."
            rows={6}
          />

          {/* Navigation */}
          <div className="navigation-buttons">
            <button onClick={handlePrevious} disabled={currentIndex === 0}>Previous</button>
            {currentIndex < questions.length - 1 ? (
              <button onClick={handleNext}>Next</button>
            ) : (
              <button onClick={handleSubmit}>Submit</button>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Interview;
