import React, { useState, useRef } from 'react';
import { Container, Typography, TextField, Button, Box } from '@mui/material';

const Contact = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const formRef = useRef(null); // Reference for the form

  const scriptUrl = "https://script.google.com/macros/s/AKfycbwrAvv5JDrNegqff4uj4j0PIhUhb7kEPhhrlFx85iwV2WM77gRZxZHt13mrlHInJW4S/exec";

  const handleSubmit = (e) => {
    e.preventDefault();

 
    const formData = new FormData(formRef.current);
    formData.append('Name', name);
    formData.append('Email', email);
    formData.append('Message', message);

    fetch('https://api.sheetbest.com/sheets/eb9ed14e-d3a4-45d9-8316-4b8e1ed00661', {
      method: 'POST',
      body: formData,
    })
    .then(res => res.json())
    .then(data => {
      console.log("Successfully submitted:", data);
      // Reset the form on success (optional)
      setName('');
      setEmail('');
      setMessage('');
    })
    .catch(err => console.log("Error:", err));
  };

  return (
    <Container maxWidth="sm" style={{ padding: '2rem' }}>
      <Box>
        <Typography variant="h4" gutterBottom>
          Contact Us
        </Typography>
        <Typography variant="body1" paragraph>
          If you have any questions or feedback, feel free to reach out to us.
        </Typography>

        {/* Add form element and ref to it */}
        <form ref={formRef} onSubmit={handleSubmit}>
          <TextField
            fullWidth
            label="Your Name"
            variant="outlined"
            margin="normal"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <TextField
            fullWidth
            label="Your Email"
            variant="outlined"
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TextField
            fullWidth
            label="Your Message"
            variant="outlined"
            margin="normal"
            multiline
            rows={4}
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <Button
            variant="contained"
            color="primary"
            style={{ marginTop: '1rem' }}
            type="submit"
          >
            Send Message
          </Button>
        </form>
      </Box>
    </Container>
  );
};

export default Contact;
