import React, { useState } from 'react';
import axios from 'axios';
import './ResumeUpload.css'; 

function ResumeUpload() {
  const [resume, setResume] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');

  // Handle file selection
  const handleFileChange = (e) => {
    if (e.target.files.length > 0) {
      setResume(e.target.files[0]);
      setUploadStatus('');
    }
  };

  // Handle drag & drop
  const handleDragOver = (e) => e.preventDefault();

  const handleDrop = (e) => {
    e.preventDefault();
    if (e.dataTransfer.files.length > 0) {
      setResume(e.dataTransfer.files[0]);
      setUploadStatus('');
    }
  };

  // Upload file
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!resume) {
      setUploadStatus('Please select a file first');
      return;
    }

    const formData = new FormData();
    formData.append('file', resume);

    try {
      console.log('Uploading file:', resume.name, resume.size); // Debug
      const response = await axios.post(
        'http://localhost:8080/resume/upload',
        formData
      );

      const data = response.data;
      console.log('Server response:', data);
      setUploadStatus(`Name: ${data.name}\nEmail: ${data.email}\nPhone: ${data.phone}\n`);
    } catch (error) {
      console.error('Error uploading file:', error);
      setUploadStatus('Failed to upload resume');
    }
  };

  return (
    <div className="upload-container">
      <h2><center>Upload Your Resume</center></h2>
      <form onSubmit={handleUpload} className="upload-form">
        <div 
          className="drop-zone" 
          onDragOver={handleDragOver} 
          onDrop={handleDrop}
          onClick={() => document.getElementById('file-input').click()}
        >
          <input 
            type="file" 
            id="file-input" 
            accept=".pdf,.doc,.docx" 
            onChange={handleFileChange} 
            style={{ display: 'none' }}
          />
          {resume ? (
            <p>{resume.name}</p>
          ) : (
            <p><center>Drag & drop your resume here, or click to select a file</center></p>
          )}
        </div>
        <button type="submit">Upload</button>
      </form>
      {uploadStatus && <p style={{ whiteSpace: 'pre-line' }}>{uploadStatus}</p>}
    </div>
  );
}

export default ResumeUpload;
