import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useRecordWebcam } from 'react-record-webcam';
import { BsDownload, BsCameraVideoFill, BsCameraVideoOffFill } from "react-icons/bs";
import cameof from "../Images/cameof.jpg";
import CameraPreview from './CameraPreview'; // Import the CameraPreview component

const Interview = () => {
  const { subject } = useParams();
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const recordWebcam = useRecordWebcam({ frameRate: 60 });

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.post(
          'https://asia-south1-language.googleapis.com/v1/models/text-bison-001:generateText', 
          {
            prompt: 'Generate Interview questions for ${subject}',
            maxOutputTokens: 150,
            temperature: 0.9,  
            candidateCount: 10 
          },
          {
            headers: {
              'Content-Type': 'application/json',
              'Authorization': 'YOUR_API_KEY', 
            },
          }
        );

        const aiQuestions = response.data.candidates.map(candidate => candidate.output.trim());
        setQuestions(aiQuestions);
      } catch (err) {
        setError('Failed to load questions. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [subject]);

  const saveFile = async () => {
    const blob = await recordWebcam.getRecording();
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'recording.webm';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const stopCamera = () => {
    if (recordWebcam.status === 'OPEN') {
      recordWebcam.close();
    }
  };

  const nextQuestion = () => {
    setCurrentQuestionIndex((prevIndex) => (prevIndex + 1) % questions.length);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', padding: '20px' }}>
      {/* Left Side: Camera Preview */}
      <div style={{ width: '45%' }}>
        <CameraPreview /> {/* Camera preview component */}
      </div>

      {/* Right Side: Interview Panel */}
      <div style={{ width: '45%', display: 'flex', flexDirection: 'column', alignItems: 'left', position: 'relative' }}>
        {/* Buttons Container */}
        <div className="bg-cyan-800 shadow-lg py-6 rounded" style={{ position: 'absolute', left: '20px', borderLeft: '200px', zIndex: 10 }}>
          <div className="flex flex-col space-y-4 py-2">
            <button onClick={recordWebcam.open} className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600">
              <BsCameraVideoFill />
            </button>
            <button onClick={stopCamera} className="bg-yellow-500 text-white px-4 py-2 rounded-md hover:bg-yellow-600">
              <BsCameraVideoOffFill />
            </button>
            <button onClick={recordWebcam.start} className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600">
              Start recording
            </button>
            <button onClick={recordWebcam.stop} className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600">
              Stop recording
            </button>
            <button onClick={saveFile} className="bg-purple-500 text-white px-4 py-2 rounded-md hover:bg-purple-600">
              <BsDownload />
            </button>
            <button onClick={nextQuestion} className="bg-orange-500 text-white px-4 py-2 rounded-md hover:bg-orange-600">
              Next Question
            </button>
          </div>
        </div>

Harsha KL, [11/7/2024 8:38 PM]
{/* Video Preview */}
        <div className="mt-4 border-rose-700" style={{ width: '100%' }}>
          {recordWebcam.status === 'CLOSED' ? (
            <div>
              <img src={cameof} alt="Camera off" className="w-full max-w-md mx-auto" />
              <p>Camera is off. Please turn on the camera.</p>
            </div>
          ) : (
            <video 
              ref={recordWebcam.webcamRef} 
              autoPlay 
              muted 
              className="w-full max-w-md mx-auto" 
              onLoadedMetadata={() => {
                if (recordWebcam.status === 'OPEN') {
                  recordWebcam.start();
                }
              }} 
            />
          )}
        </div>

        <p className="text-center mt-2 text-white">Camera status: {recordWebcam.status}</p>

        {loading ? (
          <p>Loading questions...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : (
          <div className="text-center text-white mt-4">
            <h2>Current Question:</h2>
            <p>{questions[currentQuestionIndex]}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Interview;
