import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { GoogleGenerativeAI } from "@google/generative-ai";
import { useRecordWebcam } from 'react-record-webcam';
import { BsDownload } from "react-icons/bs";
import CameraPreview from './CameraPreview'; // Import your Camera preview component

const Interview = () => {
  const { subject } = useParams();
  const location = useLocation();
  const resumeText = location.state?.resumeText || '';
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GOOGLE_API_KEY);
  const recordWebcam = useRecordWebcam({ frameRate: 60 });

  /*const fetchQuestions = async () => {
    setLoading(true);
    try {
      const model = genAI.getGenerativeModel({ model: "text-bison-001" });
      const prompt = Based on the resume content: "${resumeText}" and the subject: ${subject}, generate a list of interview questions.;
      const result = await model.generateContent(prompt);
      const response = await result.response;
      const text = await response.text();
      const generatedQuestions = text.split('\n').filter(q => q);
      setQuestions(generatedQuestions);
    } catch (error) {
      console.error("Error generating questions:", error);
      setError("Failed to load questions. Please try again later.");
    } finally {
      setLoading(false);
    }
  };*/

  useEffect(() => {
    fetchQuestions();
  }, [subject, resumeText]);

  const saveFile = async () => {
    const blob = await recordWebcam.getRecording();
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'recording.webm';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const nextQuestion = () => {
    setCurrentQuestionIndex((prevIndex) => (prevIndex + 1) % questions.length);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', padding: '20px' }}>
      <div style={{ width: '45%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        {/* Use CameraPreview to handle camera feed */}
        <CameraPreview />

        
      </div>

      <div style={{ width: '45%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        {loading ? (
          <p>Loading questions...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : (
          <div className="text-center text-white mt-4">
            <h2>Current Question:</h2>
            <p>{questions[currentQuestionIndex]}</p>
            <button onClick={nextQuestion} className="bg-orange-500 text-white px-4 py-2 rounded-md hover:bg-orange-600 mt-4">
              Next Question
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Interview;
