import React, { useState } from 'react';
import axios from 'axios';
import './ResumeUpload.css';
import { useNavigate } from 'react-router-dom';

function ResumeUpload() {
  const [resumeFile, setResumeFile] = useState(null);
  const [resumeData, setResumeData] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const navigate = useNavigate();

  // File selection
  const handleFileChange = (e) => {
    if (e.target.files.length > 0) {
      setResumeFile(e.target.files[0]);
      setUploadStatus('');
      setResumeData(null);
      setInterviewQuestions([]);
    }
  };

  // Drag & drop
  const handleDragOver = (e) => e.preventDefault();
  const handleDrop = (e) => {
    e.preventDefault();
    if (e.dataTransfer.files.length > 0) {
      setResumeFile(e.dataTransfer.files[0]);
      setUploadStatus('');
      setResumeData(null);
      setInterviewQuestions([]);
    }
  };

  // Upload file
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!resumeFile) {
      setUploadStatus('Please select a file first');
      return;
    }

    const formData = new FormData();
    formData.append('file', resumeFile);

    try {
      const response = await axios.post(
        'http://localhost:8080/resume/upload',
        formData
      );

      setResumeData(response.data);
      setUploadStatus('Resume uploaded successfully!');
    } catch (error) {
      console.error('Error uploading file:', error);
      setUploadStatus('Failed to upload resume');
    }
  };

  

  return (
    <div className="upload-container">
      <h2>Upload Your Resume</h2>
      <form onSubmit={handleUpload} className="upload-form">
        <div
          className="drop-zone"
          onDragOver={handleDragOver}
          onDrop={handleDrop}
          onClick={() => document.getElementById('file-input').click()}
        >
          <input
            type="file"
            id="file-input"
            accept=".pdf,.doc,.docx"
            onChange={handleFileChange}
            style={{ display: 'none' }}
          />
          {resumeFile ? (
            <p>{resumeFile.name}</p>
          ) : (
            <p>Drag & drop your resume here, or click to select a file</p>
          )}
        </div>
        <button type="submit">Upload</button>
      </form>

      {uploadStatus && <p className="status">{uploadStatus}</p>}

      {resumeData && (
        <>
          <div className="resume-data">
            <div className="resume-section">
              <h3>Basic Info</h3>
              <p><strong>Name:</strong> {resumeData.name}</p>
              <p><strong>Email:</strong> {resumeData.email}</p>
              <p><strong>Phone:</strong> {resumeData.phone}</p>
              {resumeData.linkedin && <p><strong>LinkedIn:</strong> {resumeData.linkedin}</p>}
              {resumeData.github && <p><strong>GitHub:</strong> {resumeData.github}</p>}
            </div>

            {resumeData.skills && resumeData.skills.length > 0 && (
              <div className="resume-section">
                <h3>Skills</h3>
                <ul>{resumeData.skills.map((s, i) => <li key={i}>{s}</li>)}</ul>
              </div>
            )}

            {resumeData.experience && resumeData.experience.length > 0 && (
              <div className="resume-section">
                <h3>Experience</h3>
                {resumeData.experience.map((exp, i) => (
                  <div key={i} className="card">
                    <p><strong>{exp.title}</strong> at {exp.company} ({exp.duration})</p>
                    <ul>{exp.details.map((d, j) => <li key={j}>{d}</li>)}</ul>
                  </div>
                ))}
              </div>
            )}

            {resumeData.projects && resumeData.projects.length > 0 && (
              <div className="resume-section">
                <h3>Projects</h3>
                {resumeData.projects.map((p, i) => (
                  <div key={i} className="card">
                    <p><strong>{p.name}</strong> ({p.techStack.join(', ')})</p>
                    <ul>{p.description.map((d, j) => <li key={j}>{d}</li>)}</ul>
                  </div>
                ))}
              </div>
            )}

            {resumeData.education && resumeData.education.length > 0 && (
              <div className="resume-section">
                <h3>Education</h3>
                {resumeData.education.map((edu, i) => (
                  <p key={i}>{edu.degree}, {edu.institution}</p>
                ))}
              </div>
            )}

            {resumeData.certifications && resumeData.certifications.length > 0 && (
              <div className="resume-section">
                <h3>Certifications</h3>
                <ul>{resumeData.certifications.map((c, i) => <li key={i}>{c}</li>)}</ul>
              </div>
            )}
          </div>

          {/* Take Interview Button */}
          <div className="take-interview">
            <button onClick={handleTakeInterview}>Take Interview</button>
          </div>
    
};


        </>
      )}
    </div>
  );
}

export default ResumeUpload;
