import React, { useEffect, useRef, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

import { useRecordWebcam } from 'react-record-webcam';
import { BsDownload, BsCameraVideoFill, BsCameraVideoOffFill } from "react-icons/bs";
import cameof from "../Images/cameof.jpg";

const Interview = () => {
  const { subject } = useParams();
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GOOGLE_API_KEY);
  const recordWebcam = useRecordWebcam({ frameRate: 60 });
  const videoRef = useRef(null);

  const fetchQuestions = async () => {
    setLoading(true);
    try {
      const model = genAI.getGenerativeModel({ model: "text-bison-001" });
      const prompt = `Generate a list of interview questions for the subject: ${subject}. The questions should cover essential topics, be insightful, and test understanding of key concepts.`;
      const result = await model.generateContent(prompt);
      const response = await result.response;
      const text = await response.text();
      const generatedQuestions = text.split('\n').filter(q => q);  // Split text by lines to separate questions
      setQuestions(generatedQuestions);
    } catch (error) {
      console.error("Error generating questions:", error);
      setError("Failed to load questions. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error('Error accessing the camera: ', error);
      }
    };

    startCamera();

    return () => {
      if (videoRef.current && videoRef.current.srcObject) {
        const stream = videoRef.current.srcObject;
        const tracks = stream.getTracks();
        tracks.forEach(track => track.stop());
      }
    };
  }, []);

  const saveFile = async () => {
    const blob = await recordWebcam.getRecording();
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'recording.webm';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const stopCamera = () => {
    if (recordWebcam.status === 'OPEN') {
      recordWebcam.close();
    }
  };

  const nextQuestion = () => {
    setCurrentQuestionIndex((prevIndex) => (prevIndex + 1) % questions.length);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', padding: '20px' }}>
      <div style={{ width: '45%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <div className="mt-4 border-rose-700" style={{ width: '100%' }}>
          {recordWebcam.status === 'CLOSED' ? (
            <div>
              <img src={cameof} alt="Camera off" className="w-full max-w-md mx-auto" />
              <p>Camera is off. Please turn on the camera.</p>
            </div>
          ) : (
            <video 
              ref={videoRef}
              autoPlay 
              muted 
              className="w-full max-w-md mx-auto" 
              onLoadedMetadata={() => {
                if (recordWebcam.status === 'OPEN') {
                  recordWebcam.start();
                }
              }}
            />
          )}
        </div>

        <div className="flex flex-col space-y-4 py-2 mt-4">
          <button onClick={recordWebcam.open} className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600">
            <BsCameraVideoFill /> Start Camera
          </button>
          <button onClick={stopCamera} className="bg-yellow-500 text-white px-4 py-2 rounded-md hover:bg-yellow-600">
            <BsCameraVideoOffFill /> Stop Camera
          </button>
          <button onClick={recordWebcam.start} className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600">
            Start Recording
          </button>
          <button onClick={recordWebcam.stop} className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600">
            Stop Recording
          </button>
          <button onClick={saveFile} className="bg-purple-500 text-white px-4 py-2 rounded-md hover:bg-purple-600">
            <BsDownload /> Download
          </button>
        </div>
      </div>

      <div style={{ width: '45%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        {loading ? (
          <p>Loading questions...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : (
          <div className="text-center text-white mt-4">
            <h2>Current Question:</h2>
            <p>{questions[currentQuestionIndex]}</p>
            <button onClick={nextQuestion} className="bg-orange-500 text-white px-4 py-2 rounded-md hover:bg-orange-600 mt-4">
              Next Question
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Interview;
