import React, { useState } from 'react';
import { Container, Typography, TextField, Button, Box, CircularProgress ,Alert} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false); // Track loading state
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault(); 
    
    const loginUser = {
      username,
      password,
    };
  
    setLoading(true); 

    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginUser),
      });
  
      if (!response.ok) {
        const text = await response.text();
        console.error("Error response:", text);
        alert('Login failed: ' + text);
        setLoading(false);
        return;
      }
  
      // Parse the JSON response
      const data = await response.json();
      console.log("Response data:", data); 
      console.log("Message:", data.message); 
  
      
      if (data.message === "Login successful") {
        alert(data.message);
        navigate('/Dashboard'); 
      } else {
        alert('Unexpected response');
      }
    } catch (error) {
      console.error("Error:", error);
      alert('Error: ' + error.message);
    } finally {
      setLoading(false); // Reset loading state
    }
  };
  
  return (
    <Container maxWidth="sm" style={{ padding: '2rem' }}>
      <Box component="form" onSubmit={handleLogin}>
        <Typography variant="h4" gutterBottom>
          Login
        </Typography>
        {success && <Alert severity="success">Logged in  successfully!</Alert>}
        {error && <Alert severity="error">Login Failer... Please try again later.</Alert>}
        <TextField
          fullWidth
          label="Username"
          variant="outlined"
          margin="normal"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <TextField
          fullWidth
          label="Password"
          type="password"
          variant="outlined"
          margin="normal"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button
          variant="contained"
          color="primary"
          type="submit" // Makes this button submit the form
          style={{ marginTop: '1rem' }}
          disabled={loading} // Disable the button while loading
        >
          {loading ? <CircularProgress size={24} /> : 'Login'}
        </Button>
      </Box>
    </Container>
  );
};

export default Login;
