import React, { useEffect, useRef, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useRecordWebcam } from 'react-record-webcam';
import { BsDownload } from "react-icons/bs";

const Interview = () => {
  const { subject } = useParams();
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  
  const recordWebcam = useRecordWebcam({ frameRate: 60 });
  const videoRef = useRef(null);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.post(
          'https://api-url',
          {
            prompt: `Generate Interview questions for ${subject}`,
            maxOutputTokens: 150,
            temperature: 0.9,  
            candidateCount: 10 
          },
          {
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${process.env.REACT_APP_GOOGLE_API_KEY}`,
            },
          }
        );
        const aiQuestions = response.data.candidates.map(candidate => candidate.output.trim());
        setQuestions(aiQuestions);
      } catch (err) {
        setError('Failed to load questions. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    
    fetchQuestions();
  }, [subject]);

  const saveFile = async () => {
    if (recordWebcam.status === 'RECORDING') {
      await recordWebcam.stop();
    }
    const blob = await recordWebcam.getRecording();
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'recording.webm';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', padding: '20px' }}>
      <div style={{ width: '45%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <div style={{ width: '100%' }}>
          <video 
            ref={videoRef} 
            autoPlay 
            muted 
            className="w-full max-w-md mx-auto"
            onLoadedMetadata={() => recordWebcam.start()}
          />
        </div>
        
        <div className="flex flex-col space-y-4 py-2 mt-4">
          <a onClick={saveFile} className="bg-purple-500 text-white px-4 py-2 rounded-md hover:bg-purple-600 cursor-pointer">
            <BsDownload /> Download
          </a>
        </div>
        
        <p className="text-center mt-2 text-white">Camera status: {recordWebcam.status}</p>
      </div>
      
      <div style={{ width: '45%', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        {loading ? (
          <p>Loading questions...</p>
        ) : error ? (
          <p className="text-red-500">{error}</p>
        ) : (
          <div className="text-center text-white mt-4">
            <h2>Current Question:</h2>
            <p>{questions[currentQuestionIndex]}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Interview;
