import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { useRecordWebcam } from 'react-record-webcam';
import { BsDownload } from "react-icons/bs";
import CameraPreview from './CameraPreview'; 

const Interview = () => {
  const { subject } = useParams();
  const location = useLocation();
  const resumeText = location.state?.resumeText || '';
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  const recordWebcam = useRecordWebcam({ frameRate: 60 });

  // Fetch questions from backend when the component is mounted
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/interview/generateQuestion?topic=${subject}`);
        if (response.ok) {
          const data = await response.json();

          // Ensure the backend returned questions
          if (data.message === "Success" && data.questions) {
            // Split the questions based on the newlines to show them one by one
            const questionArray = data.questions.split("\n\n");
            setQuestions(questionArray);
            setLoading(false);
          } else {
            setError("No questions available");
            setLoading(false);
          }
        } else {
          setError("Failed to load questions");
          setLoading(false);
        }
      } catch (err) {
        setError("Error fetching questions");
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [subject]);  // Re-run when subject changes
  
  const saveFile = async () => {
    const blob = await recordWebcam.getRecording();
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'recording.webm';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const nextQuestion = () => {
    setCurrentQuestionIndex((prevIndex) => (prevIndex + 1) % questions.length);
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', padding: '20px', backgroundColor: '#f4f4f9' }}>
      <div style={{ width: '45%', height:'40',display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '10px' }}>
        <CameraPreview />
      </div>

      <div style={{ width: '45%', display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px', backgroundColor: '#ffffff', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)' }}>
        {loading ? (
          <p style={{ fontSize: '18px', color: '#333' }}>Loading questions...</p>
        ) : error ? (
          <p style={{ color: '#e74c3c', fontSize: '18px' }}>{error}</p>
        ) : (
          <div className="text-center text-black mt-4">
            <h2 style={{ fontSize: '24px', fontWeight: 'bold', color: '#333', marginBottom: '20px' }}>Current Question:</h2>
            <p style={{ fontSize: '18px', lineHeight: '1.6', color: '#555', textAlign: 'center' }}>{questions[currentQuestionIndex]}</p>
            <button 
              onClick={nextQuestion} 
              style={{ 
                backgroundColor: '#f39c12', 
                color: '#fff', 
                padding: '10px 20px', 
                borderRadius: '6px', 
                border: 'none', 
                cursor: 'pointer', 
                marginTop: '20px', 
                fontSize: '16px',
                transition: 'background-color 0.3s ease'
              }}
              onMouseOver={(e) => e.target.style.backgroundColor = '#e67e22'}
              onMouseOut={(e) => e.target.style.backgroundColor = '#f39c12'}
            >
              Next Question
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Interview;
