import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import './Interview.css'; // For styling the interview page

function Interview() {
  const location = useLocation();
  const { resumeData } = location.state || {};
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    if (resumeData) {
      generateQuestions();
    }
  }, [resumeData]);

  const generateQuestions = async () => {
    try {
      const response = await axios.post(
        'http://localhost:8080/interview/generate',
        resumeData
      );
      setQuestions(response.data);
    } catch (error) {
      console.error('Error generating interview questions:', error);
    }
  };

  if (!resumeData) return <p>No resume data found. Please upload a resume first.</p>;

  return (
    <div className="interview-container">
      <h2>Interview Session</h2>
      <div className="resume-info">
        <p><strong>Name:</strong> {resumeData.name}</p>
        <p><strong>Email:</strong> {resumeData.email}</p>
        <p><strong>Skills:</strong> {resumeData.skills?.join(', ')}</p>
      </div>

      <div className="questions-section">
        <h3>Generated Questions</h3>
        {questions.length === 0 ? (
          <p>Loading questions...</p>
        ) : (
          <ul>
            {questions.map((q, index) => (
              <li key={index}>
                <strong>[{q.difficulty}]</strong> {q.question}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Interview;
